$(document).ready(function () {
  // fetch per aggiornare i dati personali

  // modulo di cambio username

  document
    .getElementById("changeUsernameForm")
    .addEventListener("submit", function (event) {
      event.preventDefault();
      sendData(this);
    });

  // modulo di cambio email
  document
    .getElementById("changeEmailForm")
    .addEventListener("submit", function (event) {
      event.preventDefault();
      sendData(this);
    });

  // modulo di cambio password
  document
    .getElementById("changePasswordForm")
    .addEventListener("submit", function (event) {
      event.preventDefault();
      sendData(this);
    });

  function sendData(form) {
    fetch("php/updateSelf.php", {
      method: "POST",
      body: new FormData(form),
    })
      .then((response) => response.text())
      .then((data) => {
        if (data == "Username empty") {
          document.getElementById("risultatoUser").innerText =
            "Username can't be empty";
        } else if (data == "Username invalid") {
          document.getElementById("risultatoUser").innerText =
            "Invalid username";
        } else if (data == "Username updated") {
          document.getElementById("risultatoUser").innerText = "";
          Swal.fire({
            position: "center",
            icon: "success",
            title: "Username changed successfully",
            showConfirmButton: false,
            timer: 1500,
          });
        } else if (data == "Email empty") {
          document.getElementById("risultatoEmail").innerText =
            "Email can't be empty";
        } else if (data == "Email used") {
          document.getElementById("risultatoEmail").innerText =
            "Email already in use";
        } else if (data == "Email updated") {
          document.getElementById("risultatoEmail").innerText = "";
          Swal.fire({
            position: "center",
            icon: "success",
            title: "Email changed successfully",
            showConfirmButton: false,
            timer: 1500,
          });
        } else if (data == "Password empty") {
          document.getElementById("risultatoPassword").innerText =
            "Password can't be empty";
        } else if (data == "Password not match") {
          document.getElementById("risultatoPassword").innerText =
            "Password doesn't match";
        } else if (data == "Password updated") {
          document.getElementById("risultatoPassword").innerText = "";
          Swal.fire({
            position: "center",
            icon: "success",
            title: "Password changed successfully",
            showConfirmButton: false,
            timer: 1500,
          });
          const passwordInputs = document.querySelectorAll(
            'input[type="password"]'
          );

          passwordInputs.forEach((input) => {
            input.value = "";
          });
        }
        console.log(data);
      })
      .catch((error) => console.error("Error:", error));
  }
});
